upload_form" enctype="multipart/form-data" method="post">        <input type="file" name="upload_data" id="upload_data"><br><br>        <ul id="upload_ul">        </ul><br>        <!--<input type="button" class="INPUT-BTN" value="Upload File" onclick="upload()">-->        <progress id="progressBar" value="0" max="100" style="width:300px;"></progress>        <!--<h3 id="status"></h3>-->        <p id="loaded_n_total"></p>    </form></div><?php $Url = base_url() . 'cad/upload_attachment'; ?><script>    $(document).ready(function () {        $("#popup-modal-div").dialog({            autoOpen: false,            resizable: false,            position: 'center',            width: 'auto',            modal: false        });        $('.upload-file').click(function () {            // e.preventDefault();            $('#popup-modal-div').dialog('open');        });        $(".delete-files").on("click", function () {            var file_name = $(this).attr("files_name");            var textboxval = $("#files_name").val();            textboxval = textboxval.replace(file_name + ',', '');            textboxval = textboxval.replace(',' + file_name, '');            textboxval = textboxval.replace(file_name, '');            $("#files_name").val(textboxval);            $(this).parent('li').remove();        });    });    $(function ($) {        $("#upload_data").on("change", function () {            upload();        }); // You should remove (jQuery) because you don't want to call the function here    });    function _(el) {        return document.getElementById(el);    }    function upload() {        var file = _("upload_data").files[0];        // alert(file.name+" | "+file.size+" | "+file.type);        var formdata = new FormData();        formdata.append("file1", file);        var ajax = new XMLHttpRequest();        ajax.upload.addEventListener("progress", progresshandler, false);        ajax.addEventListener("load", completehandler, false);        ajax.addEventListener("error", errorhandler, false);        ajax.addEventListener("abort", aborthandler, false);        ajax.open("POST", "<?php echo $Url; ?>");        ajax.send(formdata);    }    function progresshandler(event) {        //_("loaded_n_total").innerHTML = "Uploaded " + event.loaded + " bytes of " + event.total;        var percent = (event.loaded / event.total) * 100;        _("progressBar").value = Math.round(percent);//        _("status").innerHTML = Math.round(percent) + "% uploaded... please wait";    }    function completehandler(event) {        var response = event.target.responseText;        var res = $.parseJSON(response);        var newlink = document.createElement('a');        newlink.appendChild(document.createTextNode('x'));        newlink.setAttribute('class', 'delete-files');        newlink.setAttribute('file_name', res.savedfilename);        var node = document.createElement("LI");                 // Create a <li> node        var textnode = document.createTextNode(res.filename);         // Create a text node        node.appendChild(textnode);        node.appendChild(newlink);                              // Append the text to <li>        document.getElementById("upload_ul").appendChild(node);//        _("images_ul").appendChild(node);        $("#files_name").val($("#files_name").val() + res.savedfilename + ",");        _("progressBar").value = 0;    }    function errorhandler(event) {        _("status").innerHTML = "Upload Failed";    }    function aborthandler(event) {        _("status").innerHTML = "Upload Aborted";    }</script>