d("file1", file);        var ajax = new XMLHttpRequest();        ajax.upload.addEventListener("progress", progressHandler, false);        ajax.addEventListener("load", completeHandler, false);        ajax.addEventListener("error", errorHandler, false);        ajax.addEventListener("abort", abortHandler, false);        ajax.open("POST", "<?php echo $Url; ?>");        ajax.send(formdata);    }    function progressHandler(event) {        //_("loaded_n_total").innerHTML = "Uploaded " + event.loaded + " bytes of " + event.total;        var percent = (event.loaded / event.total) * 100;        _("progressBar").value = Math.round(percent);//        _("status").innerHTML = Math.round(percent) + "% uploaded... please wait";    }    function completeHandler(event) {        var response = event.target.responseText;        var res = $.parseJSON(response);        var newlink = document.createElement('a');        newlink.appendChild(document.createTextNode('x'));        newlink.setAttribute('class', 'delete-files');        newlink.setAttribute('file_name', res.savedfilename);        var node = document.createElement("LI");                 // Create a <li> node        var textnode = document.createTextNode(res.filename);         // Create a text node        node.appendChild(textnode);        node.appendChild(newlink);                              // Append the text to <li>        document.getElementById("images_ul").appendChild(node);//        _("images_ul").appendChild(node);        $("#file_names").val($("#file_names").val() + res.savedfilename + ",");        _("progressBar").value = 0;    }    function errorHandler(event) {        _("status").innerHTML = "Upload Failed";    }    function abortHandler(event) {        _("status").innerHTML = "Upload Aborted";    }//	$(document).ready(function(){//		$(".delete-files").live("click", function(){//			var file_name = $(this).attr("file_name");//			var  textboxval = $("#file_names").val();//			textboxval = textboxval.replace(file_name+',','');//			textboxval = textboxval.replace(','+file_name,'');//			textboxval = textboxval.replace(file_name,'');//			$("#file_names").val(textboxval);//			$(this).parent('li').remove();//		});//	});</script>